    private static final char[] MIDDLES = {'_', 'x', 'X'};
    private static final Random random = new Random();

    public static String generateGameNickname() {
        String prefix = PREFIXES[random.nextInt(PREFIXES.length)];
        String base = BASES[random.nextInt(BASES.length)];
        char mid = MIDDLES[random.nextInt(MIDDLES.length)];

        String num = "";
        if (random.nextDouble() < 0.3) {
            int type = random.nextInt(4);
            num = switch (type) {
                case 0 -> String.valueOf(random.nextInt(10));
                case 1 -> String.valueOf(random.nextInt(100));
                case 2 -> String.valueOf(random.nextInt(999));
                default -> String.valueOf(random.nextInt(9000) + 1000);
            };
        }

        if (random.nextBoolean()) prefix = prefix.toLowerCase();
        if (random.nextBoolean()) base = base.toLowerCase();

        String nickname = switch (random.nextInt(3)) {
            case 0 -> prefix + mid + base + num;
            case 1 -> base + mid + prefix + num;
            default -> prefix + base + num;
        };
        if (nickname.length() > 15) {
            nickname = nickname.substring(0, 15);
        }
        return nickname;
    }
